INSERT INTO `news` (`news_id`, `title`, `short_description`, `long_description`, `image`, `faculty_id`, `category_id`, `status`, `date`, `year`, `month`, `timestamp`) VALUES
(1, 'ঢাবি-এ তাজউদ্দীন আহমদ স্মারক বক্তৃতা ৮ নভেম্বর ২০১৮', '', 'বাংলাদেশের প্রথম প্রধানমন্ত্রী, মহান মুক্তিযুদ্ধের অন্যতম সংগঠক, জাতীয় নেতা তাজউদ্দীন আহমদের জন্মবার্ষিকী স্মরণে “তাজউদ্দীন আহমদ স্মারক বক্তৃতা” অনুষ্ঠান আগামীকাল ৮ নভেম্বর ২০১৮ বৃহস্পতিবার বিকাল ৩টায় ঢাকা বিশ্ববিদ্যালয়ের নবাব নওয়াব আলী চৌধুরী সিনেট ভবনে অনুষ্ঠিত হবে। \r\n\r\nউক্ত অনুষ্ঠানে বিশ্ববিদ্যালয়ের উপাচার্য অধ্যাপক ড. মো. আখতারুজ্জামান প্রধান অতিথি হিসেবে উপস্থিত থাকবেন। বাংলাদেশ জাতীয় সংসদের সদস্য ও তাজউদ্দীন আহমদ মেমোরিয়াল ট্রাস্ট ফান্ডের ট্রাস্টি বোর্ডের সদস্য সিমিন হোসেন রিমি, ঢাকা বিশ্ববিদ্যালয়ের প্রো-ভাইস চ্যান্সেলর (শিক্ষা) অধ্যাপক ড. নাসরীন আহমাদ ও প্রো-ভাইস চ্যান্সেলর (প্রশাসন) অধ্যাপক ড. মুহাম্মদ সামাদ বিশেষ অতিথি হিসেবে উপস্থিত থাকবেন। এতে বিশ্ববিদ্যালয়ের কোষাধ্যক্ষ ও তাজউদ্দীন আহমদ মেমোরিয়াল ট্রাস্ট ফান্ডের ট্রাস্টি বোর্ডের চেয়ারম্যান অধ্যাপক ড. মো: কামাল উদ্দীনের সভাপতিত্বে অনুষ্ঠেয় উক্ত অনুষ্ঠানে অধ্যাপক তাজউদ্দীন আহমদ স্মারক বক্তৃতা প্রদান করবেন ঢাকা বিশ্ববিদ্যালয় ইতিহাস বিভাগের বঙ্গবন্ধু চেয়ার অধ্যাপক ড. মুনতাসীর মামুন।\r\n\r\nঅনুষ্ঠানে তাজউদ্দীন আহমদ মেমোরিয়াল ট্রাস্ট ফান্ডের স্বর্ণপদক, বৃত্তি ও রচনা প্রতিযোগিতার পুরস্কারও বিতরণ করা হবে। ', 'news04.jpg', 1, 10, 0, '2018-06-20', '2018', '06', '1529445600'),
(2, 'ঢাবি ঘ (Gha)-ইউনিটের পুন:পরীক্ষার ফল প্রকাশ', '', 'ঢাকা বিশ্ববিদ্যালয় ২০১৮-২০১৯ শিক্ষাবর্ষে সামাজিক বিজ্ঞান অনুষদভুক্ত ’ঘ’ (Gha) ইউনিটের    ১ম বর্ষ স্নাতক সম্মান শ্রেণির পুন:পরীক্ষার ফল আজ ১৯ নভেম্বর ২০১৮ সোমবার বিকেল ৫:০০টায় প্রকাশিত হয়েছে। পরীক্ষার বিস্তারিত ফলাফল ও ভর্তি সংক্রান্ত গুরুত্বপূর্ণ সকল তথ্য বিশ্ববিদ্যালয়ের admission.eis.du.ac.bd ওয়েবসাইটে ‘ঘ’ ইউনিটের নোটিশ থেকে জানা যাবে। তাছাড়া, যে কোন অপারেটরের মোবাইল ফোন থেকে DU GHA ˂roll no˃ টাইপ করে ১৬৩২১ নম্বরে send করে ফিরতি SMS-এ ফলাফল পাওয়া যাবে।\r\n \r\nউল্লেখ্য, গত ১৬ নভেম্বর ২০১৮ শুক্রবার এই ভর্তি পরীক্ষা অনুষ্ঠিত হয়। মোট ১৮ হাজার ৪৬৩জন পরীক্ষার্থীর মধ্যে ১৬ হাজার ১৮১জন পরীক্ষায় অংশগ্রহণ করে। এর মধ্যে উত্তীর্ণ হয়েছে ৯ হাজার ৮৮৬জন। পাশের হার ৬১.১০%। ', 'news07.jpg', 7, 11, 0, '2018-06-20', '2018', '06', '1529445600'),
(3, 'ইউনিভার্সিটি ক্যারিয়ার ক্লাবের উদ্যোগে ক্লাব সদস্যদের দক্ষতা উন্নয়ন কর্মসূচী অনুষ্ঠান অনুষ্ঠিত', '', 'ঢাকা ইউনিভার্সিটি ক্যারিয়ার ক্লাবের উদ্যোগে আজ ১৩ নভেম্বর ২০১৮ মঙ্গলবার বাংলাদেশ শিশু একাডেমি মিলনায়তনে ক্লাব সদস্যদের দক্ষতা উন্নয়ন কর্মসূচীতে প্রধান অতিথি হিসেবে বক্তব্য রাখেন ঢাকা বিশ্ববিদ্যালয়ের উপাচার্য অধ্যাপক ড. মো. আখতারুজ্জামান।', 'news03.jpg', 3, 12, 0, '2018-06-20', '2018', '06', '1529445600'),
(4, 'আবিস্কার ও উদ্ভাবক বিষয়ক “FAB Fest 2018” অনুষ্ঠিত', '', 'ঢাকা বিশ্ববিদ্যালয়ে আজ ১০ নভেম্বর ২০১৮ শনিবার নবাব নওয়াব আলী চৌধুরী সিনেট ভবনে আবিস্কার ও উদ্ভাবক বিষয়ক “FAB Fest 2018” অনুষ্ঠিত হয়। বিশ্ববিদ্যালয়ের উপাচার্য অধ্যাপক ড. মো. আখতারুজ্জামান এতে প্রধান অতিথি হিসেবে উপস্থিত ছিলেন। অনুষ্ঠানে বিশেষ অতিথি ছিলেন উচ্চশিক্ষার মান উন্নয়ন প্রকল্পের (হেকেপ)-এর প্রজেক্ট ডিরেক্টর গৌরাঙ্গ চন্দ্র মোহান্ত এনডিসি এবং ঢাকাস্থ ব্রিটিশ কাউন্সিলের পরিচালক (শিক্ষা) ডেভিড মেনার্ড। ছবিতে উপাচার্যকে প্রধান অতিথির বক্তব্য রাখতে দেখা যাচ্ছে।', 'news04.jpg', 2, 13, 0, '2018-06-20', '2018', '06', '1529445600'),
(5, 'ঢাবি থেকে ২৩জন গবেষকের পিএইচ.ডি. এবং ১৩জনের এম.ফিল. ডিগ্রি লাভ', '', 'ঢাকা বিশ্ববিদ্যালয় থেকে সম্প্রতি ২৩জন গবেষক পিএইচ.ডি. এবং ১৩জন গবেষক এম.ফিল. ডিগ্রি লাভ করেছেন। গত ৩১ অক্টোবর ২০১৮ বুধবার ঢাকা বিশ্ববিদ্যালয়ের উপাচার্য অধ্যাপক ড. মো. আখতারুজ্জামানের সভাপতিত্বে অনুষ্ঠিত সিন্ডিকেট সভায় তাদের এই ডিগ্রি প্রদান করা হয়।  \r\n\r\nপিএইচ.ডি. ডিগ্রী প্রাপ্তরা হলেন: বাংলা বিভাগের অধীনে মোহাম্মদ কুদরত-ই-হুদা, মো. হারুনুর রশীদ, লোপামুদ্রা মালেক, ইতিহাস বিভাগের অধীনে মুর্শিদা বিন্তে রহমান, শহীদ কাদের চৌধুরী, আরবী বিভাগের অধীনে মুহাম্মদ আল আমীন, মুহাম্মদ বেলাল হোসাইন, ইসলামিক স্টাডিজ বিভাগের অধীনে মুহাম্মদ আনোয়ারুল হক, লোক প্রশাসন বিভাগের অধীনে মো. মইনুর রহমান চৌধুরী, গোলাম শফিউদ্দিন, গণযোগাযোগ ও সাংবাদিকতা বিভাগের অধীনে শেখ মোহাম্মদ শফিউল ইসলাম, শান্তি ও সংঘর্ষ অধ্যয়ন বিভাগের অধীনে খান ফেরদৌসর রহমান, বায়োমেডিকেল ফিজিক্স এন্ড টেকনোলজি বিভাগের অধীনে মো. জিহাদ তরফদার, মৃত্তিকা, পানি ও পরিবেশ বিভাগের অধীনে মোহাম্মদ মনিরুজ্জামান, মনোবিজ্ঞান বিভাগের অধীনে মোসলেহা পারভীন, অণুজীব বিজ্ঞান বিভাগের অধীনে শাহিনা আক্তার, এডুকেশনাল এন্ড কাউন্সেলিং সাইকোলজি বিভাগের অধীনে মেহজাবীন হক, ফার্মাসিউটিক্যাল কেমিস্ট্রি বিভাগের অধীনে মুহাম্মদ ইব্রাহীম, ফলিত রসায়ন ও কেমিকৌশল বিভাগের অধীনে ফেরদৌস আক্তার, মার্কেটিং বিভাগের অধীনে মোহাম্মদ আবু বকর সিদ্দিক, মো. তাবারক হোসেন ভূঁঞা, ব্যবসায় প্রশাসন ইনস্টিটিউটের অধীনে সৈয়দ মুনতাসির মামুন এবং সমাজকল্যাণ ও গবেষণা ইনস্টিটিউটের অধীনে শাহানাজ পারভীন। \r\n\r\nএম.ফিল. ডিগ্রী প্রাপ্তরা হলেন: বাংলা বিভাগের অধীনে ফারজানা আক্তার, ইতিহাস বিভাগের অধীনে সনজিদা খানম, দর্শন বিভাগের অধীনে সজীব কুমার বসু, আরবী বিভাগের অধীনে আবদুল করিম, তথ্যবিজ্ঞান ও গ্রন্থাগার ব্যবস্থাপনা বিভাগের অধীনে মো. শফিউর রহমান, মোহাম্মদ বুরহান উদ্দিন, সৈয়দা মুক্তা বেগম, লোক প্রশাসন বিভাগের অধীনে মো. সাইফুল ইসলাম, উন্নয়ন অধ্যয়ন বিভাগের অধীনে আনোয়ার হোসেন চৌধুরী, ভূগোল ও পরিবেশ বিভাগের অধীনে সুরাইয়া আক্তার, প্রাচ্যকলা বিভাগের অধীনে মোহাম্মদ আবদুর রহীম, শিক্ষা ও গবেষণা ইনস্টিটিউটের অধীনে পারভিন আক্তার এবং পুষ্টি ও খাদ্য বিজ্ঞান ইনস্টিটিউটের অধীনে ইউমনা বুশরা।', 'news08.jpg', 5, 14, 0, '2018-06-20', '2018', '06', '1529445600'),
(6, 'ঢাবি-এ সেন্টার ফর জেনোসাইড স্টাডিজের পোস্ট গ্র্যাজুয়েট ডিপ্লোমা কোর্সের সনদপত্র বিতরণ', '', 'ঢাকা বিশ্ববিদ্যালয় সেন্টার ফর জেনোসাইড স্টাডিজের ‘পোস্ট গ্র্যাজুয়েট ডিপ্লোমা’ কোর্স এর ৮ম ব্যাচের শিক্ষার্থীদের সনদপত্র বিতরণ করা হয়েছে। বিশ্ববিদ্যালয়ের উপাচার্য অধ্যাপক ড. মো. আখতারুজ্জামান প্রধান অতিথি হিসেবে উপস্থিত থেকে শিক্ষার্থীদের মাঝে সনদপত্র বিতরণ করেন। এই সনদপত্র বিতরণ অনুষ্ঠান গতকাল ১৭ নভেম্বর ২০১৮ শনিবার উচ্চতর মানবিক ও সামাজিক বিজ্ঞান গবেষণা কেন্দ্র ভবনস্থ জেনোসাইড স্টাডিজ সেন্টারের কনফারেন্স রুমে অনুষ্ঠিত হয়েছে। সেন্টার ফর জেনোসাইড স্টাডিজের পরিচালক অধ্যাপক ড. ইমতিয়াজ আহমেদের সভাপতিত্বে অনুষ্ঠিত এই অনুষ্ঠানে উপস্থিত ছিলেন আন্তর্জাতিক সম্পর্ক বিভাগের ভারপ্রাপ্ত চেয়ারম্যান অধ্যাপক ড. রাশেদ উজ জামান।\r\nপ্রধান অতিথির বক্তব্যে উপাচার্য কোর্সে অংশগ্রহনকারীদের অভিনন্দন জানিয়ে বলেন, পাকিস্তান কর্তৃক ১৯৭১ এর গণহত্যার একটি প্রধান কেন্দ্র ছিল ঢাকা বিশ্ববিদ্যালয়। তৎকালীন ইকবাল হল, জগন্নাথ হল, ঢাকা বিশ্ববিদ্যালয় ক্লাব সহ পুরো ক্যাম্পাসেই গণহত্যা হয়েছে। যেকোন গণহত্যাই বর্বরোচিত। এ বিষয়ে বিশদভাবে জানতে গবেষণা ও প্রশিক্ষণ দরকার। সাধারন মানুষ ও শিক্ষার্থীদের মাঝে গণহত্যা বিষয়ে সচেতনতা তৈরিতে এই সেন্টার বিশেষ ভূমিকা রাখতে পারে বলে উপাচার্য আশা প্রকাশ করেন।', 'news06.jpg', 6, 10, 0, '2018-06-20', '2018', '06', '1529445600'),
(7, 'ঢাবি’র ৩৩ জন অবসরপ্রাপ্ত শিক্ষককে সংবর্ধনা প্রদান', '', 'ঢাকা বিশ্ববিদ্যালয় শিক্ষক সমিতি কর্তৃক বিশ্ববিদ্যালয়ের বিভিন্ন বিভাগের ৩৩ জন অবসরপ্রাপ্ত শিক্ষককে সংবর্ধনা প্রদান করা হয়েছে। গতকাল ১৭ নভেম্বর ২০১৮ শনিবার সন্ধ্যায় বিজনেস স্টাডিজ অনুষদের ড. হাবিবুল্লাহ সম্মেলন কক্ষে এক বর্ণাঢ্য অনুষ্ঠানে এই সংবর্ধনা প্রদান করা হয়। ঢাকা বিশ্ববিদ্যালয়ের উপাচার্য অধ্যাপক ড. মো. আখতারুজ্জামান অনুষ্ঠানে প্রধান অতিথি হিসেবে উপস্থিত থেকে বিদায়ী শিক্ষকদের ক্রেস্ট, মানপত্র ও ফুলেল শুভেচ্ছা প্রদান করেন।\r\nশিক্ষক সমিতির সভাপতি অধ্যাপক ড. এ এস এম মাকসুদ কামালের সভাপতিত্বে অনুষ্ঠিত এতে অন্যান্যের মধ্যে বক্তব্য রাখেন প্রো-ভাইস চ্যান্সেলর (শিক্ষা) অধ্যাপক ড. নাসরীন আহমাদ, প্রো-ভাইস চ্যান্সেলর (প্রশাসন) অধ্যাপক ড. মুহাম্মদ সামাদ ও কোষাধ্যক্ষ অধ্যাপক ড. মো. কামাল উদ্দীন। অনুষ্ঠান সঞ্চালনা ও মানপত্র পাঠ করেন সমিতির সাধারণ সম্পাদক অধ্যাপক শিবলী রুবাইয়াতুল ইসলাম। অবসরপ্রাপ্ত শিক্ষকদের মধ্য থেকে কয়েকজন তাঁদের কর্মজীবনের বিভিন্ন স্মৃতিচারণ করেন। এসময় বিভিন্ন অনুষদের ডিন, বিভিন্ন বিভাগের চেয়ারম্যান এবং বিভিন্ন হলের প্রাধ্যক্ষ উপস্থিত ছিলেন।\r\nপ্রধান অতিথির বক্তব্যে উপাচার্য অধ্যাপক ড. মো. আখতারুজ্জামান অবসরপ্রাপ্ত শিক্ষকদের উদ্দেশ্যে বলেন, ‘বিশ্ববিদ্যালয়ে আপনাদের শূন্যতা অপূরণীয়। শিক্ষা ও গবেষণার ক্ষেত্রে আপনাদের অবদান স্মরণীয় হয়ে থাকবে। ঢাকা বিশ্ববিদ্যালয়ের সাথে আপনাদের সম্পর্ক ও সম্পৃক্ততা সবসময় থাকবে। আপনাদের জীবন ঘনিষ্ঠ অভিজ্ঞতা আমাদের পরবর্তী জীবনে কাজে লাগবে।’ মহান স্বাধীনতা আন্দোলন ও মুক্তিযুদ্ধকালীন সময়ের কথা উল্লেখ করে উপাচার্য বলেন, ‘আপনারা একটি বিশেষ যুগের সৃষ্টি এবং স্বাক্ষী। ঐতিহাসিক এসব আন্দোলন-সংগ্রামে আপনাদের সম্পৃক্ততা ঢাকা বিশ্ববিদ্যালয় সম্মানের সাথে স্মরণ রাখবে।’\r\nসংবর্ধনাপ্রাপ্ত শিক্ষকরা হলেন- অধ্যাপক ড. ফরিদা জামান (অংকন  ও চিত্রায়ণ), অধ্যাপক ড. আজিজুর রহমান (মনোবিজ্ঞান), অধ্যাপক ড. মো. আমিনুল হক ভূঁইয়া (পুষ্টি ও খাদ্য বিজ্ঞান ইনস্টিটিউট), অধ্যাপক ড. খান মওদুদ উর-রহমান (পুষ্টি ও খাদ্য বিজ্ঞান ইনস্টিটিউট), অধ্যাপক ড. মোহাম্মদ মহিউদ্দিন (ম্যানেজমেন্ট), অধ্যাপক এম শাহজান মিনা (ফিন্যান্স), অধ্যাপক ড. মুনীরউদ্দিন আহমদ (ক্লিনিক্যাল ফার্মেসী ও ফার্মাকোলজী), অধ্যাপক ড. উম্মে কুলসুম রওজাতুর রোম্মান (রসায়ন), অধ্যাপক ড. মু. মুস্তাফা আলম (ভূতত্ত্ব), অধ্যাপক ড. মুহাম্মদ আব্দুল বাকী (ইসলামিক স্টাডিজ), অধ্যাপক মিসেস খালেদা এদিব (পুষ্টি ও খাদ্য বিজ্ঞান ইনস্টিটিউট), অধ্যাপক ড. শাকের আহমেদ (ট্যুরিজম এন্ড হসপিটালিটি ম্যানেজমেন্ট), অধ্যাপক ড. এম ফরিদ আহমেদ (ফিন্যান্স), অধ্যাপক ড. এস এম মাহফুজুর রহমান (ইন্টারন্যাশনাল বিজনেস), অধ্যাপক আ ব ম ফারুক (ওষুধ প্রযুক্তি), অধ্যাপক ড. হোসনে জাহান বেগম (পদার্থ বিজ্ঞান), অধ্যাপক ড. শারমিন হক (শিক্ষা ও গবেষণা ইনস্টিটিউট), অধ্যাপক ড. এ এম মাসুদুজ্জামান (শিক্ষা ও গবেষণা ইনস্টিটিউট), অধ্যাপক ড. আহাদুজ্জামান মোহম্মদ আলী (গণযোগাযোগ ও সাংবাদিকতা), অধ্যাপক ড. মেহতাব খানম (এডুকেশনাল এন্ড কাউন্সেলিং সাইকোলজি), অধ্যাপক ড. প্রদীপ কুমার রায় (দর্শন), অধ্যাপক ড. চৌধুরী রফিকুল আবরার (আন্তর্জাতিক সম্পর্ক), অধ্যাপক ড. মো. তজিবর রহমান (গণিত), অধ্যাপক ড. এ কে এম নুর-উন-নবী (পপুলেশন সায়েন্সেস), অধ্যাপক ড. মো. আজহারুল ইসলাম (শিক্ষা ও গবেষণা ইনস্টিটিউট), অধ্যাপক আখতার সুলতানা (গণযোগাযোগ ও সাংবাদিকতা), অধ্যাপক ড. মো. তৌফিকুল হায়দার (ইসলামের ইতিহাস ও সংস্কৃতি), অধ্যাপক ড. বেগম আকতার কামাল (বাংলা), অধ্যাপক মো. আব্দুর রউফ (আইন), অধ্যাপক ড. আ হ ম আবদুল বাকী (ভূগোল ও পরিবেশ), অধ্যাপক ড. মিসেস মমতাজ বেগম (উদ্ভিদ বিজ্ঞান), সহযোগী অধ্যাপক মোল্লা শহীদুজ্জামান (ইংরেজি) এবং অধ্যাপক মিসেস লুৎফুন নাহার (পুষ্টি ও খাদ্য বিজ্ঞান ইনস্টিটিউট)। ', 'news07.jpg', 8, 11, 0, '2018-06-20', '2018', '06', '1529445600'),
(8, 'ঢাকা বিশ্ববিদ্যালয়ে রোকেয়া স্মারক বক্তৃতা অনুষ্ঠিত', '', 'ঢাকা বিশ্ববিদ্যালয় উইমেন এন্ড জেন্ডার স্টাডিজ বিভাগের উদ্যোগে ’রোকেয়া স্মারক বক্তৃতা’ গতকাল ১৯ নভেম্বর ২০১৮ সোমবার অধ্যাপক মোজাফফর আহমেদ চৌধুরী মিলনায়তনে অনুষ্ঠিত হয়। ঢাকা বিশ্ববিদ্যালয়ের উপাচার্য অধ্যাপক ড. মো. আখতারুজ্জামান অনুষ্ঠানে প্রধান অতিথি হিসেবে উপস্থিত ছিলেন। \r\n\r\nউইমেন এন্ড জেন্ডার স্টাডিজ বিভাগের চেয়ারপার্সন ড. সানজীদা আখতারের সভাপতিত্বে অনুষ্ঠানে সামাজিক বিজ্ঞান অনুষদের ডিন অধ্যাপক ড. সাদেকা হালিম বিশেষ অতিথি হিসেবে বক্তব্য রাখেন। ’রোকেয়ার এক বিস্মৃত অনুসারী’ শীর্ষক স্মারক বক্তৃতা প্রদান করেন জাতীয় অধ্যাপক ড. আনিসুজ্জামান।\r\n\r\nউপাচার্য অধ্যাপক ড. মো. আখতারুজ্জামান বেগম রোকেয়ার স্মৃতির প্রতি গভীর শ্রদ্ধা নিবেদন করেন। তিনি বলেন, বেগম রোকেয়ার জীবন ও কর্মকে শুধু একাডেমিক চর্চার মধ্যে আবদ্ধ রাখলে চলবেনা,  আমাদের প্রায়োগিক জীবনেও তার দর্শনকে কাজে লাগাতে হবে। উন্নয়নের মূল ধারায় নারীদের সম্পৃক্ত করার ওপর গুরুত্বারোপ করে তিনি বলেন, কর্ম-পরিকল্পনা প্রণয়নের ক্ষেত্রে নারীদের স্বার্থকে বিবেচনা করতে হবে। নারীর ক্ষমতায়ন ও নারী অধিকার নিশ্চিত করতে শিক্ষাকে একমাত্র হাতিয়ার বলে তিনি উল্লেখ করেন।\r\n\r\nঅনুষ্ঠানে উইমেন এন্ড জেন্ডার স্টাডিজ বিভাগের ১৪জন কৃর্তী শিক্ষার্থীকে বৃত্তি প্রদান করা হয়। এরমধ্যে ৪ জন ’অধ্যাপক আসাদুজ্জামান স্মৃতি বৃত্তি’, ৩ জন ’মামুনা খাতুন স্মৃতি বৃত্তি’, ২ জন ’অর্নব স্মৃতি বৃত্তি’ এবং ৫ জন ’নীলা বৃত্তি’ লাভ করেন।', 'news08.jpg', 4, 12, 0, '2018-06-20', '2018', '06', '1529445600');

